    onComboboxSelect211: function(combo, record, eOpts) {
        //in use

        //STORE
        var stUser = Ext.data.StoreManager.get("StUser");
        var StTarif = Ext.data.StoreManager.get("StTarifOt");
        var tarif = Ext.getCmp('tarTeplo');
        var viborTarif = Ext.getCmp('cbTarifTeplo');


        //LOGIN & PASSWORD

        var values =stUser.getAt(0);
        var login = values.get('login');
        var password = values.get('password');

        //LOGIKA
        if (record) {
            StTarif.load({
                params: {
                    what:'Tarif_Ot',
                    login:login,
                    password:password,
                    what_id: record.get('raion_id')
                },
                callback: function(records,operation,success){
                    if(success){
                        viborTarif.clearValue();
                        viborTarif.setDisabled(true);
                        tarif.setValue(0);
                    }
                },
                scope:this
            });
        }
    },

    onCbTarifTeploSelect: function(combo, record, eOpts) {
        //in use

        //STORE
        var form = combo.findParentByType('form');
        var tarif = form.getForm().findField('tar');
        //var values =StTarif.getAt(0);
        var grid = Ext.getCmp('grTarifHousesPodogrev');
        var btnInsTarif = Ext.getCmp('btnInsTarifTeplo');
        var selected = record;
        //console.log(records[0]);

        if (record) {
            var gridRowSelectedRecords  = grid.getView().getSelectionModel().getSelection()[0];
            var size = Ext.Object.getSize(gridRowSelectedRecords) ;
            btnInsTarif.setDisabled(false);

            tarif.setValue(gridRowSelectedRecords.get(record.get('tarif')));

        }

    }

