Ext.define('Ykis.view.sprav.TabReportOrgViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.tabreportorg',

    onPanelBeforeClose: function(panel, eOpts) {
        var tabPnCenter = Ext.getCmp('tabPnCenter');
        tabPnCenter.child('#tabLogin').tab.hide();

        var act = 0;
        Ext.Array.forEach(tabPnCenter.getTabBar().items.items, function(f)
        {
            if (f.hidden===false) {
                act = act + 1;
            }
        });
        //console.log(act);
        if (act < 2) {
            tabPnCenter.setActiveTab('tabLogin');
        }

    }

});
