    onRowEditingEdit: function(editor, context, eOpts) {
        var grid = editor.grid;
        var store = grid.getStore();
        var sm = store.getUpdatedRecords();
        var data = grid.getSelectionModel();
        if(sm.length) {

            store.sync({

                success: function(){
                    store.load();
                },
                scope:this
            });
        }

    },

    onComboboxSelect211: function(combo, record, eOpts) {
        var stUser = Ext.data.StoreManager.get("StUser");
        var values =stUser.getAt(0);
        var login = values.get('login');
        var password = values.get('password');
        var StUtszn = Ext.data.StoreManager.get("StUtszn");

        var btnImportUtszn =  Ext.getCmp('btnImportUtszn');
        //var btnLgotaUtszn =  Ext.getCmp('btnLgotaUtszn');
        var btnUpdateReestrUtszn =  Ext.getCmp('btnUpdateReestrUtszn');
        var btnPrintReestrToUtszn =  Ext.getCmp('btnPrintReestrToUtszn');
        var btnExportUtszn =  Ext.getCmp('btnExportUtszn');


        var data =  Ext.getCmp('dataNachUtszn').getValue();
        var lgota =  Ext.getCmp('lgota').getValue().vibor;
        if (record) {
            //console.log(record.get('edrpou'));

            values.set({'org_id':record.get('edrpou')});
            stUser.sync();
            btnImportUtszn.setDisabled(false);
            //btnLgotaUtszn.setDisabled(false);

            btnUpdateReestrUtszn.setDisabled(false);
            btnPrintReestrToUtszn.setDisabled(false);
            btnExportUtszn.setDisabled(false);




            StUtszn.load({
                params: {
                    what:'utszn',
                    login:values.get('login'),
                    password:values.get('password'),
                    data:data,
                    lgota:lgota,
                    org_id:record.get('edrpou')
                },
                scope:this
            });
        }

    }

