    onBtnFmLgotaPererClick: function(button, e, eOpts) {
        //this.up('#winAddVodomer').close();
        var form = button.findParentByType('form').getForm();
        //form.reset();
        var tarif = form.findField('tarif').getValue();
        var qty = form.findField('qty').getValue();
        var percent = form.findField('percent').getValue();
        var people = form.findField('people').getValue();

        var summa = 0;


        if (tarif===0) {

            Ext.MessageBox.show({
                title: 'Проверка тарифа',
                msg: 'Введите тариф ',
                buttons: Ext.MessageBox.OK,
                icon: Ext.MessageBox.INFO
            });
        } else {
            summa = Math.round((qty * tarif * percent / 100)*100)/100 ;
            form.findField('summa').setValue(summa);
        }
    }

