    onChkExistentVodChange: function(field, newValue, oldValue, eOpts) {
        var stUser = Ext.data.StoreManager.get("StUser");
        var values =stUser.getAt(0);
        var login = values.get('login');
        var password = values.get('password');
        var house_id = values.get('house_id');
        var vibor = values.get('vibor');
        var cnt = Ext.getCmp('fcntNewVodomer');
        var cb = Ext.getCmp('cbExistentVod');
        var form = field.findParentByType('form');
        var store = Ext.data.StoreManager.get("StExistentVod");
        cb.clearValue();
        var what = "";
        if (vibor === "addAppVodomer") {
            what = "ExistentVodomer";
        }else{
            what = "ExistentVod";
            var house_id = Ext.getCmp('cbHouseFilial').getValue();

        }
        if (newValue) {

            store.load({
                params: {
                    what:what,
                    house_id: house_id,
                    login:login,
                    password:password
                },
                scope:this
            });
            cnt.hide();
            cb.show();
            cb.clearValue();
            form.down('#btAddVodomer').setDisabled(true);
        }
        else {
            cnt.show();
            cb.hide();
            form.down('#btAddVodomer').setDisabled(false);
        }
    }

